svg.script:
"use strict";

var fg = new MultiFG().setup();
var draw = new FGDraw(fg);
var leftUrl = fg.getRequiredParameter("left");
var rightUrl = fg.getRequiredParameter("right");

function init(evt) {
    fg.load(function() {
			var leftFG = new FG("l", 0);
			fg.registerFG(leftFG);
			var stackFramesLeft = new FGStackFrames();
			stackFramesLeft.loadCollapsed(leftFG, leftUrl, function() {
				var rightFG = new FG("r", leftFG.width + fg.margin);
				fg.registerFG(rightFG);
				var stackFramesRight = new FGStackFrames();
				stackFramesRight.loadCollapsed(rightFG, rightUrl, function() {
					fg.setDimensions();
					draw.drawCanvas();
					var drawLeft = new FGDraw(leftFG);
					drawLeft.drawFG(stackFramesLeft);
					drawLeft.drawInfoElements();
					var drawRight = new FGDraw(rightFG);
					drawRight.drawFG(stackFramesRight);
					drawRight.drawInfoElements();
				}, function(response) {
					draw.drawError("Failed to load collapsed file " + rightUrl + ": " + response.errorMessage());
				});
			}, function(response) {
				draw.drawError("Failed to load collapsed file " + leftUrl + ": " + response.errorMessage());
			});
	}, function(response) {
		draw.drawError("Failed to load: " + response.errorMessage());
	});
}
